.footer {
  padding-top: 30px;
  padding-bottom: 43px;
  background-color: $second-background;
}

.footer__first-text {
  font-family: 'Titan One';
  @include fonts(18px, 1.17, $second-accent);
  text-transform: uppercase;
  letter-spacing: normal;
  margin-bottom: 17px;
  @media screen and (min-width: $second-change) {
    @include fonts(26px, 1.15, $second-accent);
    margin-bottom: 16px;
  }
  @media screen and (min-width: $second-change) {
    @include fonts(30px, 1.13, $third-change);
    margin-bottom: 15px;
  }
}

.footer__second-text {
  font-family: 'Titan One';
  @include fonts(34px, 1.15, $main-accent);
  text-transform: uppercase;
  color: $main-accent;
  @media screen and (min-width: $second-change) {
    @include fonts(48px, 1.15, $main-accent);
    letter-spacing: 0.06em;
  }
  @media screen and (min-width: $second-change) {
    @include fonts(58px, 1.14, $main-accent);
  }
}

.social-networks {
  display: flex;
  justify-content: right;
  margin-bottom: 46px;
}

.social-networks__item:not(:first-child) {
  margin-left: 29px;
}

.social-networks__icon {
  fill: $footer-icon;
  @media screen and (min-width: $second-change) {
    width: 34px;
    height: 34px;
  }
}
.social-networks__icon:hover,
.social-networks__icon:focus {
  fill: $main-accent;
}

.footer__contacts {
  font-family: 'DM Sans';
  font-weight: bold;
  @include fonts(12px, 1.86, $second-text);
  text-align: right;
  @media screen and (min-width: $second-change) {
    font-size: 14px;
  }
}

.footer__contacts--link {
  color: $main-accent;
}

.footer__flex-box {
  @media screen and (min-width: $second-change) {
    display: flex;
    justify-content: space-between;
  }
}

@media screen and (max-width: $second-change) {
  .footer__flex-box--text {
    margin-bottom: 43px;
  }
}
@media screen and (min-width: $second-change) and (max-width: $third-change) {
  .footer__flex-box--text {
    padding-top: 7px;
  }
}

.headline {
  padding-top: 22px;
  padding-bottom: 25px;

  @include fonts(14px, 1.52, $third-text);
  font-weight: bold;
  text-align: center;
  background-color: $second-background;

  border-top: 1px solid $footer-line;
}
.footer__br {
  @media screen and (min-width: $third-change) {
    display: none;
  }
}

.headline__br {
  @media screen and (min-width: $second-change) {
    display: none;
  }
}
